mapper.select:
select
t.classid,t.classname,t.leafflag
from  pub_goods_class t,
pub_goods_classtype t1
where
t.classtypeid = t1.classtypeid
and t1.entryid = #{entryid}
and t1.classtypeid=#{classtypeid}
order by t.classid

select d.goodsid, floor(nvl(sum(a.goodsqty-nvl(c.dtlgoodsqty,0)),0)) goodsqty from bms_st_qty_lst a, gpcs_placepoint b, bms_st_io_dtl_tmp c, pub_goods d where d.goodsid = a.goodsid(+) and a.storageid = b.storageid(+) and a.batchid = c.batchid(+) and a.goodsstatusid(+) = 1 and b.entryid(+) = #{entryid} and b.placepointid(+) = #{placepointid} and d.goodsid in
#{item}
group by d.goodsid

select a.goodsid, a.price from ngpcs_all_price a, gpcs_placepoint b where a.placepointid = b.placepointid and b.placepointid=#{placepointid} and b.entryid = #{entryid} and a.priceid=#{priceid}
and a.sys_modifydate > to_date(#{lasteventtime},'YYYYMMDDHH24MISS') - 1/96
and a.goodsid in
#{item}

select distinct t.goodsid from bms_st_io_doc t where t.storageid = (select storageid from gpcs_placepoint a where a.placepointid = #{placepointid}) and t.credate > to_date(#{lasteventtime},'YYYYMMDDHH24MISS') - 1/96 and t.goodsid in
#{item}
union select distinct t.goodsid from bms_st_io_doc_tmp t where t.storageid = (select storageid from gpcs_placepoint a where a.placepointid = #{placepointid}) and t.credate > to_date(#{lasteventtime},'YYYYMMDDHH24MISS') - 1/96 and t.goodsid in
#{item}
