mapper.select:
select d.iodtlid, f.invno, f.invcode, to_char(f.invdate,'YYYY-MM-DD') invdate, e.supplyname, g.entryname, e.total_in, f.susetdtlid, f.total_line, h.zx_contractid shtxdh from bms_sa_doc a, bms_sa_dtl b, bms_st_io_doc c, bms_st_io_dtl d, bms_su_set_doc e, bms_su_set_dtl f, pub_entry g, bms_su_dtl h where a.salesid = b.salesid and b.salesdtlid = c.sourceid and c.comefrom = 3 and c.inoutid = d.inoutid and d.batchid = f.zx_batchid(+) and f.susetdocid = e.susetdocid(+) and f.sudocdtlid = h.sudocdtlid(+) and a.entryid = g.entryid
and a.entryid = #{entryid}
and a.salesid = #{salesid}
