mapper.select:
SELECT
FROM (SELECT ROWNUM rowno, a.goodsid, b.goodsname, b.goodsunit, b.goodstype, b.currencyname, c.factoryname, a.entryid, nvl(a.barcode, b.barcode) barcode, a.sys_modifydate FROM pub_entry_goods a, pub_goods b, pub_factory c where a.goodsid = b.goodsid and b.factoryid = c.factoryid
and a.entryid = #{entryid}
and a.goodsid = #{goodsid}
and a.sys_modifydate > to_date(#{sys_modifydate},'YYYY-MM-DD HH24:MI:SS')
and ROWNUM <= 100*#{ym}) table_alias WHERE table_alias.rowno > 100*(#{ym}-1)

SELECT entrycompanyid, goodsid, goodsname, goodsunit, goodstype, currencyname, factoryname, entryid, barcode, sys_modifydate FROM (select ROWNUM rowno, t.entryid, t.entrycompanyid, a.goodsid, b.goodsname, b.goodsunit, b.goodstype, b.currencyname, c.factoryname, nvl(a.barcode, b.barcode) barcode, to_char(a.sys_modifydate, 'YYYY-MM-DD HH24:MI:SS') sys_modifydate from pub_entry t, pub_entry_goods a, pub_goods b, pub_factory c where t.entryid in (224, 283, 285, 287, 289, 291, 293, 295, 363, 384) and t.entryid = a.entryid and a.goodsid = b.goodsid
and b.factoryid = c.factoryid
and a.goodsid = #{goodsid}
and t.entrycompanyid = #{entrycompanyid}
and a.sys_modifydate > to_date(#{sys_modifydate},'YYYY-MM-DD HH24:MI:SS')
and ROWNUM <= 100*#{ym}) table_alias WHERE table_alias.rowno > 100*(#{ym}-1)
